sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
   //Type appropriate comment here, and begin script below

	var currentUser = g_user.userID;
	var role = currentUser.hasRole();
	g_form.clearOptions('manager');

	if(role == 'x_1151148_car_park.service manager'){
		if(isLoading && !g_form.isNewRecord()){
			g_form.setValue('manager', currentUser.name);
			g_form.setValue('self_service_work', 'Service');
    }
}
}
